mapper.select:
SELECT password
  FROM AP_USER
 WHERE id = #{userId}
  AND email = #{email}

mapper.delete:
DELETE FROM AP_SCHEDULE
      WHERE INT_NAME = #{INT_NAME}
      AND TITLE = #{TITLE}

delete from AP_STATISTIC 
	where product_number = #{CERTNO} 
		and item_number = #{ITEMNO}

mapper.update:
UPDATE ap_user_role
	SET password=#{password}
WHERE user_id=#{user_id}
